mapper.update:
UPDATE MEMBER
SET MEMBER_PASS = #{memberPass}, MEMBER_EMAIL = #{memberEmail}, MEMBER_NAME = #{memberName}, MEMBER_PHONE = #{memberPhone}, MEMBER_ADDRESS = #{memberAddress}
WHERE MEMBER_ID = #{memberId}

mapper.select:

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_PASS = #{memberPass}


SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.update:
UPDATE MEMBER
SET MEMBER_SUB = #{memberSub}
WHERE MEMBER_ID = #{memberId}
